$navbar-height: 3.25rem;
$navbar-color: #487C84;
$cta-background: #487C84;
$font-black: #0b0b0b;
$alt-section-background:#f5f5f5;
$kates-brand: #ffe600;

html{
    scroll-behavior: smooth;
}

body{
    font-family: 'Roboto', sans-serif;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
}

img{
    border-radius:3px;
}

section{
    &.alt{
        background-color:$alt-section-background;
    }
    &.disclaimer{
        padding:2rem;
        background-color:#6a4884;
        @media only screen and (min-width: 768px) {
            padding:4rem;
        }
        a{
            text-decoration: underline;
            color:#fff;
        }
        h2{
            font-family: 'Roboto', sans-serif;
            font-size:1.2rem;
            line-height:1.6rem;
            margin-bottom:0;
            color:#fff;
            .fa-paw{
                display:none;
                @media only screen and (min-width: 768px) {
                    display:inline-block;
                }
            }
            @media only screen and (min-width: 768px) {
                font-size:1.8rem;
                
            }
        }

    }
}

.button {
    background: $cta-background;
    border-color: $cta-background;
    color: #fff;
    transition: all .4s;
    &:hover{
        transform: translateY(-2px);
        box-shadow: 0 1rem 2rem rgba(0, 0, 0, 0.2);
        color:#fff;
        border-color:$cta-background;
    }
}

.content{
    h2, h3, h4, strong{
        font-family: 'Lobster', cursive;
    }
    h2{
        font-size:2.4rem;
        @media only screen and (min-width: 768px) {
            font-size:3.2rem;
            
        }
    }
    strong{
        font-size:1.4rem;
        @media only screen and (min-width: 768px) {
            font-size:1.75rem;
        }
    }
    p{
        color: $font-black;
    }
    ul{
        li{
            margin-bottom:2rem;
        }
    }
}

.navbar{
    min-height: $navbar-height;
    background: $navbar-color;
    transition: all 0.2s ease;
    .navbar-brand{
        .navbar-burger{
            color:#fff;
            span{
                height:2px;
            }
        }
    }
    .navbar-item{
        color:#fff;
        font-size:1.15rem;
        &:hover, &:focus, &:focus-within{
            background: #41737b;
            color:#fff;
        }
    }
    &.fixed{
        position: fixed;
        top: 0;
        left: 0;
        animation: smoothScroll .5s forwards;
        width:100%;
    }

    .navbar-menu{
        background-color:#44767d;
        padding-bottom:0;
        @media only screen and (min-width: 768px) {
           background-color:$navbar-color;
           padding-bottom:auto;
        }
        .navbar-start{
            .navbar-item{
                border-bottom:1px solid #3b646a;
                @media only screen and (min-width: 768px) {
                    border-bottom:none;
                }
            }
        }
        .navbar-end{
            @media only screen and (max-width: 768px) {
                background-color:#252525;
            }
        }
    }
}
.hero{
    background-image: url("../img/whippetmedium.jpg");
    background-color:#205c72;
    background-size: cover;
    background-position: center;
    box-shadow: inset 0 0 0 2000px rgba(0, 0, 0, 0.6);
    .hero-inner{
        flex-direction: column;
        display:flex;
        align-items: center;
        justify-content: space-evenly;
        @media only screen and (min-width: 768px) {
            flex-direction: row;
        }
        &__left{
            img{
                width:130px;
                @media only screen and (min-width: 768px) {
                    width:auto;
                }
            }
        }
        &__right{
            @media only screen and (min-width: 768px) {
                padding: 0 5rem;
            }
            .content{
                h1{
                    font-size:1.6rem;
                    @media only screen and (min-width: 768px) {
                        font-size:3rem;
                        margin-bottom:0;
                    }
                    span{
                        font-family: 'Lobster', cursive;
                        @media only screen and (min-width: 768px) {
                            font-size:5rem;
                        }
                    }
                }
                h1, p{
                    color:#fff;
                }
                p{
                    font-size:1.2rem;
                    @media only screen and (min-width: 768px) {
                        font-size:1.4rem;
                    }
                }
            }
            .social-container{
                flex-direction:column;
                display: flex;
                justify-content: space-between;
                margin:1.5rem 0;
                @media only screen and (min-width: 768px) {
                    flex-direction: row;
                    align-items: center;
                }
                &__links{
                    margin: 1.5rem 0px 0 0px;
                    display: flex;
                    align-items: center;
                    @media only screen and (min-width: 768px) {
                        margin:0;
                    }
                    span{
                        font-weight:bold;
                        color:#fff;
                    }
                    .fa-facebook, .fa-instagram{
                        font-size:1.8rem;
                        margin-left: 1rem;
                        color:#fff;
                    }
                } 
            }   
        }
    }
}

.card{
    .card-image{
        position: relative;
        height:285px;
        width:100%;
        background-size: cover;
        background-position: center;
        &--first{
            background-image: url("../img/dogwalk-forest.jpg");
        }
        &--second{
            background-image: url("../img/cat.jpg");
        }
        &--third{
            background-image: url("../img/senior.jpg");
        }
        .offer-container{
            position:absolute;
            right:0;
            img{
                width:9rem;
            }
        }
    }
    &-content{
        &__info{
            min-height:22rem;
        }
        &__pricing{
            display:flex;
            align-items: center;
            justify-content: space-between;
            p{
                font-size:1rem;
            }
            .cost{
                display:block;
                font-size:2rem;
                font-weight:bold;
            }
        }
    }
}
#contact-me{
    iframe{
        display:none;
        @media only screen and (min-width: 768px) {
            display:block;
        }
    }
    .content{
        ul{
            li{
                margin-bottom:0;
            }
        }
        }
}
.footer{
    padding:3rem 1.5rem 3rem;
    background-color:$navbar-color;
    p, strong{
        color:#fff;
    }
    span{
        font-weight:bold;
    }
}

/* --- keyframes  --- */
@keyframes smoothScroll {
	0% {
		transform: translateY(-40px);
	}
	100% {
		transform: translateY(0px);
	}
}
